INSERT INTO `posts` (`id`, `category_id`, `user_id`, `title`, `detail`, `thumbnail`, `post_time`, `post_url`, `views`) VALUES
(42, 5, 3, '“Thủy quái” nặng hơn 1 tạ được đưa từ vịnh Thái Lan về Hà Nội', '<p>Ng&agrave;y 16/10, một chủ nh&agrave; h&agrave;ng ở quận Cầu Giấy cho biết, tối 15/6, nh&agrave; h&agrave;ng đ&atilde; đưa con c&aacute; m&uacute; nặng 105kg từ Th&aacute;i Lan về H&agrave; Nội. Con c&aacute; m&uacute; được được ướp bằng đ&aacute; v&agrave; vận chuyển bằng đường h&agrave;ng kh&ocirc;ng về nh&agrave; h&agrave;ng.</p>\r\n\r\n<p>&ldquo;Trước đ&acirc;y ch&uacute;ng t&ocirc;i đ&atilde; từng mua được một số con c&aacute; m&uacute; đưa về H&agrave; Nội phục vụ thực kh&aacute;ch. Tuy nhi&ecirc;n, đ&acirc;y l&agrave; con c&aacute; m&uacute; &ldquo;khủng&rdquo; nhất m&agrave; ch&uacute;ng t&ocirc;i từng đưa về nh&agrave; h&agrave;ng. Hiện tại, gi&aacute; của c&aacute; m&uacute; được b&aacute;n gần 1,5 triệu đồng/1kg&rdquo;, vị chủ nh&agrave; h&agrave;ng ở H&agrave; Nội th&ocirc;ng tin.</p>\r\n\r\n<p>Theo vị n&agrave;y, con c&aacute; m&uacute; c&oacute; m&agrave;u x&aacute;m, đầu to, miệng rộng.&nbsp; C&aacute; c&oacute; chiều d&agrave;i 1,6m. S&aacute;ng c&ugrave;ng ng&agrave;y, nh&agrave; h&agrave;ng đ&atilde; thịt con c&aacute; phục vụ&nbsp;thực kh&aacute;ch.</p>\r\n\r\n<p><img alt=\"“Thủy quái” nặng hơn 1 tạ được đưa từ vịnh Thái Lan về Hà Nội - 1\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-16/1508135583-thuy-quai-vinh-thai-lan-nang-hon-1-ta-dua-ve-ha-noi-1-1508128681-width660height701.jpg\" /></p>\r\n\r\n<p>Con c&aacute; m&uacute; đ&aacute;nh bắt ở Vịnh Th&aacute;i Lan vừa được đưa về H&agrave; Nội</p>\r\n\r\n<p><img alt=\"“Thủy quái” nặng hơn 1 tạ được đưa từ vịnh Thái Lan về Hà Nội - 2\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-16/1508135583-thuy-quai-vinh-thai-lan-nang-hon-1-ta-dua-ve-ha-noi-2-1508128681-width660height440.jpg\" /></p>\r\n\r\n<p>C&aacute; m&uacute; c&oacute; c&acirc;n nặng 105kg, d&agrave;i hơn 1m.</p>\r\n\r\n<p>Theo chuy&ecirc;n gia nghi&ecirc;n cứu về thủy sản, c&aacute; m&uacute; nghệ t&ecirc;n khoa học l&agrave; Epinephelus lanceolatus. Đ&acirc;y l&agrave; lo&agrave;i c&aacute; xương lớn nhất được t&igrave;m thấy ở c&aacute;c rạn san h&ocirc;. Lo&agrave;i c&aacute; n&agrave;y sống ở v&ugrave;ng Ấn Độ Dương v&agrave; Th&aacute;i B&igrave;nh Dương. C&aacute; m&uacute; thường sống ở v&ugrave;ng nước n&ocirc;ng v&agrave; ăn nhiều lo&agrave;i thuỷ sinh ở biển, kể cả c&aacute; mập nhỏ v&agrave; r&ugrave;a biển nhỏ.</p>\r\n\r\n<p>Tại Việt Nam, một số người d&acirc;n cũng nu&ocirc;i lo&agrave;i c&aacute; m&uacute;, tuy nhi&ecirc;n, c&acirc;n nặng to nhất chỉ đạt 5-10kg.&nbsp;Năm 2016, một ngư d&acirc;n cũng đ&aacute;nh được con c&aacute; m&uacute; nặng 100kg tại C&ocirc;n Đảo (tỉnh B&agrave; Rịa Vũng T&agrave;u). Sau đ&oacute;, ngư d&acirc;n đ&atilde; b&aacute;n lại con c&aacute; m&uacute; cho một nh&agrave; h&agrave;ng ở TP. Hồ Ch&iacute; Minh. Tại khu vực Vịnh Th&aacute;i Lan, thỉnh thoảng ngư d&acirc;n cũng đ&aacute;nh bắt được c&aacute; m&uacute; c&oacute; c&acirc;n nặng từ v&agrave;i chục kg đến hơn 1 tạ.</p>\r\n\r\n<p><img alt=\"“Thủy quái” nặng hơn 1 tạ được đưa từ vịnh Thái Lan về Hà Nội - 3\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-16/1508135583-thuy-quai-vinh-thai-lan-nang-hon-1-ta-dua-ve-ha-noi-3-1508128681-width660height440.jpg\" /></p>\r\n\r\n<p>Bụng c&aacute; m&uacute; c&oacute; m&agrave;u x&aacute;m. C&aacute; m&uacute; thường sống ở v&ugrave;ng nước n&ocirc;ng v&agrave; ăn nhiều lo&agrave;i thuỷ sinh ở biển, kể cả c&aacute; mập nhỏ v&agrave; r&ugrave;a biển nhỏ.</p>\r\n\r\n<p><img alt=\"“Thủy quái” nặng hơn 1 tạ được đưa từ vịnh Thái Lan về Hà Nội - 4\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-16/1508135584-thuy-quai-vinh-thai-lan-nang-hon-1-ta-dua-ve-ha-noi-4-1508128681-width660height440.jpg\" /></p>\r\n\r\n<p>4 người mới khi&ecirc;ng nổi con c&aacute; m&uacute; &ldquo;khủng&rdquo;</p>\r\n\r\n<p><img alt=\"“Thủy quái” nặng hơn 1 tạ được đưa từ vịnh Thái Lan về Hà Nội - 5\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-16/1508135584-thuy-quai-vinh-thai-lan-nang-hon-1-ta-dua-ve-ha-noi-5-1508128681-width660height440.jpg\" /></p>\r\n\r\n<p>Phần v&acirc;y của c&aacute; m&uacute; kh&aacute; lớn.</p>\r\n', 'IMG_59e4703aa49630dccedfb095464e5b5a0e285f0e9f942.jpg', '2017-10-16 03:10:22', '', 1),
(43, 2, 3, 'MU có mới nới cũ: Mua Kane 170 triệu bảng, bán Lukaku sang Real', '<p><strong>Romelu Lukaku</strong>&nbsp;đang thể hiện phong độ ấn tượng ở m&ugrave;a giải năm nay sau khi chuyển từ Everton đến MU. Ch&acirc;n s&uacute;t người Bỉ dẫn đầu danh s&aacute;ch ghi b&agrave;n&nbsp;<a href=\"http://www.24h.com.vn/premier-league-2017-2018-c48e2367.html\">Premier League</a>&nbsp;với 7 b&agrave;n thắng. Tuy nhi&ecirc;n, trong trận gặp Liverpool, Lukaku lại g&acirc;y thất vọng khi kh&ocirc;ng thể ghi được b&agrave;n thắng n&agrave;o. M&agrave;n tr&igrave;nh diễn n&agrave;y khiến huyền thoại &ldquo;Quỷ đỏ&rdquo; Gary Neville b&agrave;y tỏ sự thất vọng v&agrave; cho rằng Lukaku chỉ l&agrave; cầu thủ của những trận đấu nhỏ.</p>\r\n\r\n<p><img alt=\"MU có mới nới cũ: Mua Kane 170 triệu bảng, bán Lukaku sang Real - 1\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-16/MU-co-moi-noi-cu-Mua-Kane-170-trieu-bang-ban-Lukaku-sang-Real-mu2-1508118547-width660height439.jpg\" /></p>\r\n\r\n<p>Nếu như MU c&oacute; được Harry Kane,&nbsp;<a href=\"http://www.24h.com.vn/real-madrid-c48e1522.html\">Real</a>&nbsp;sẽ tậu Lukaku</p>\r\n\r\n<p>Lo lắng về đẳng cấp thực sự của Lukaku, HLV Mourinho đ&atilde; đề nghị ban l&atilde;nh đạo MU mang về th&ecirc;m một ch&acirc;n s&uacute;t nữa nhằm n&acirc;ng cấp h&agrave;ng c&ocirc;ng trong tương lai.</p>\r\n\r\n<p>Cụ thể, tiền đạo m&agrave; &ldquo;Người đặc biệt&rdquo; đang nhắm đến hiện nay l&agrave; Harry Kane, người cũng đ&atilde; c&oacute; được 6 b&agrave;n cho Tottenham ở Premier League m&ugrave;a n&agrave;y. Dự kiến, MU sẽ phải trả cho đội b&oacute;ng London khoảng 170 triệu bảng cho thương vụ tr&ecirc;n v&agrave; phải trả lương cao hơn 200.000 bảng/tuần cho tiền đạo người Anh.</p>\r\n\r\n<p>Thuyền trưởng MU c&oacute; vẻ rất quyết t&acirc;m c&oacute; được Chiếc gi&agrave;y v&agrave;ng m&ugrave;a trước trong đội h&igrave;nh v&agrave; sẽ l&agrave;m tất cả để điều n&agrave;y th&agrave;nh hiện thực. Mục đ&iacute;ch của &quot;Người đặc biệt&quot;&nbsp;đưa Kane về nhằm tạo ra song s&aacute;t với Lukaku&nbsp;tr&ecirc;n h&agrave;ng c&ocirc;ng, ngo&agrave;i ra cũng để thay thế tiền đạo Ibrahimovic sẽ kết th&uacute;c hợp đồng với đội chủ s&acirc;n Old Trafford v&agrave;o cuối m&ugrave;a.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, theo El Gol Digital, việc tậu Harry Kane về c&oacute; thể khiến MU mất&nbsp;<em>Romelu Lukaku</em>&nbsp;v&agrave;o tay đội b&oacute;ng Ho&agrave;ng gia&nbsp;<a href=\"http://www.24h.com.vn/real-madrid-c48e1522.html\">Real Madrid</a>. Được biết, &ldquo;Kền kền trắng&rdquo; sẽ v&agrave;o cuộc để k&yacute; hợp đồng với ch&acirc;n s&uacute;t người Bỉ. Hiện tại, Chủ tịch Perez đang cần những gương mặt mới tr&ecirc;n h&agrave;ng tiền đạo&nbsp;sau khi Cristiano Ronaldo, Benzema, Bale đang ng&agrave;y một sa s&uacute;t trong thời gian qua.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong những&nbsp;năm trước, Real lu&ocirc;n l&agrave; &ldquo;c&aacute;o gi&agrave;&rdquo; tr&ecirc;n thị trường mua sắm. Thế nhưng, việc &ldquo;&aacute;n binh bất động&rdquo; tr&ecirc;n thị trường chuyển nhượng m&ugrave;a h&egrave; năm nay đang b&aacute;o hại HLV Zidane, đội b&oacute;ng Ho&agrave;ng gia c&oacute; sự khởi đầu kh&ocirc;ng su&ocirc;n sẻ tại La Liga khi vẫn đang bị Barcelona bỏ xa 5 điểm tr&ecirc;n BXH.</p>\r\n\r\n<p><strong>Bị huyền thoại chỉ tr&iacute;ch, Lukaku đ&aacute;p trả đanh th&eacute;p</strong></p>\r\n\r\n<p>Theo thống k&ecirc;, Lukaku phải mất trung b&igrave;nh 289 ph&uacute;t để c&oacute; được 1 b&agrave;n thắng v&agrave;o lưới c&aacute;c đội nằm trong nh&oacute;m &ldquo;Big 6&rdquo;. Nhưng&nbsp;đối với phần c&ograve;n lại của giải đấu, Lukaku chỉ mất 136 ph&uacute;t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"MU có mới nới cũ: Mua Kane 170 triệu bảng, bán Lukaku sang Real - 2\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-16/MU-co-moi-noi-cu-Mua-Kane-170-trieu-bang-ban-Lukaku-sang-Real-lu2-1508118594-width660height621.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lukaku tịt ng&ograve;i trong trận Liverpool</p>\r\n\r\n<p>Mới đ&acirc;y, huyền thoại Gary Neville đ&atilde;&nbsp;chỉ tr&iacute;ch ch&acirc;n s&uacute;t trị gi&aacute; 75 triệu bảng&nbsp;l&agrave; cầu thủ của những trận đấu nhỏ sau khi MU&nbsp;h&ograve;a 0-0 trước Liverpool tr&ecirc;n s&acirc;n Anfield. Ngay lập tức, tiền đạo người Bỉ đ&atilde; lớn tiếng khẳng định bản th&acirc;n vẫn đang chơi tốt.</p>\r\n\r\n<p>&ldquo;C&oacute; nhiều tiền đạo trong giải đấu bỏ lỡ nhiều cơ hội ngon ăn hơn t&ocirc;i&rdquo;,&nbsp;Lukaku&nbsp;chia sẻ. &ldquo;</p>\r\n', 'IMG_59e470ad464f810a30cfc7e0a9fe78b8d78d3b0bff4d0.jpg', '2017-10-16 03:10:17', '', 1),
(44, 2, 3, 'Tổng hợp Ngoại hạng Anh vòng 8 rực lửa: MU \"tàn nhẫn\", Man City tuyệt đỉnh', '<p><img alt=\"Tổng hợp Ngoại hạng Anh vòng 8 rực lửa: MU &amp;#34;tàn nhẫn&amp;#34;, Man City tuyệt đỉnh - 1\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-16/Tong-hop-Ngoai-hang-Anh-V8-ruc-lua-MU-thuc-dung-tan-nhan-Man-City-tuyet-dinh-thang-hoa-mu-1508108714-width660height371.jpg\" /></p>\r\n\r\n<p>MU v&agrave;&nbsp;<a href=\"http://www.24h.com.vn/liverpool-c48e1528.html\">Liverpool</a>&nbsp;cầm ch&acirc;n nhau kh&ocirc;ng b&agrave;n thắng</p>\r\n\r\n<p>Trong khi MU h&ograve;a tẻ nhạt với Liverpool, trận đấu của Man City th&ecirc;m 1 lần nữa lại l&agrave; trận cầu c&oacute; nhiều b&agrave;n thắng nhất v&ograve;ng đấu. D&ugrave; cho Pep Guardiola đ&atilde; trải qua &iacute;t ph&uacute;t lo lắng khi đội b&oacute;ng của &ocirc;ng để cho Stoke City r&uacute;t ngắn tỉ số xuống c&ograve;n 2-3 nhưng gần như ngay lập tức, Man City đ&atilde; c&oacute; 4 b&agrave;n thắng để nhấn ch&igrave;m đội kh&aacute;ch với tỉ số &quot;kh&ocirc;ng tưởng&quot; 7-2.</p>\r\n\r\n<p>Trong khi Tottenham c&oacute; trận thắng chật vật 1-0 tr&ecirc;n s&acirc;n nh&agrave;, cả&nbsp;<a href=\"http://www.24h.com.vn/chelsea-c48e1505.html\">Chelsea</a>&nbsp;lẫn&nbsp;<a href=\"http://www.24h.com.vn/arsenal-fc-c48e3424.html\">Arsenal</a>&nbsp;đều chịu thất bại với c&ugrave;ng tỉ số 1-2. Arsenal c&ograve;n c&oacute; thể đưa ra lời b&agrave;o chữa cho m&igrave;nh khi trận thua của họ c&oacute; nhiều t&igrave;nh huống g&acirc;y tranh c&atilde;i&nbsp;nhưng&nbsp;ĐKVĐ Chelsea th&igrave;&nbsp;chỉ c&oacute; thể tự tr&aacute;ch m&igrave;nh v&igrave; để thua đội cuối bảng, chưa c&oacute; điểm n&agrave;o, kh&ocirc;ng ghi được b&agrave;n thắng n&agrave;o - Crystal Palace.</p>\r\n\r\n<p>T&acirc;m điểm của v&ograve;ng 8 l&agrave; trận Derby nước Anh giữa Liverpool v&agrave; MU. Trận đấu đ&atilde; diễn ra đ&atilde; phản &aacute;nh đ&uacute;ng t&igrave;nh h&igrave;nh lực lượng của cả 2 đội. Liverpool thiếu vắng tiền đạo chủ lực Sadio Mane v&agrave; họ loay hoay kh&ocirc;ng biết l&agrave;m sao để ghi b&agrave;n v&agrave;o lưới MU. &quot;Quỷ đỏ&quot; th&agrave;nh Manchester v&igrave;&nbsp;kh&ocirc;ng c&oacute; sự phục vụ của bộ đ&ocirc;i tiền vệ Paul Pogba v&agrave; Fellaini n&ecirc;n họ cũng chẳng thể n&agrave;o cầm b&oacute;ng v&agrave; triển khai 1 phương &aacute;n tấn c&ocirc;ng n&agrave;o s&aacute;ng nước trong cả trận. Tỉ số h&ograve;a kh&ocirc;ng b&agrave;n thắng đ&atilde; thể hiện&nbsp;đ&uacute;ng cục diện trận đấu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Tổng hợp Ngoại hạng Anh vòng 8 rực lửa: MU &amp;#34;tàn nhẫn&amp;#34;, Man City tuyệt đỉnh - 2\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-16/Tong-hop-Ngoai-hang-Anh-V8-ruc-lua-MU-thuc-dung-tan-nhan-Man-City-tuyet-dinh-thang-hoa-mcanh-1508108714-width660height425.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Man City tiếp tục c&oacute; chiến thắng hủy diệt</p>\r\n', 'IMG_59e471883b31f8415ef408c57a2395fb86b1fd1836b90.jpg', '2017-10-16 03:10:56', NULL, 0),
(45, 2, 3, 'Thảm họa Chelsea: Lời nguyền khốc liệt, Conte lo mất ghế vào tay Ancelotti', '<p>Chưa c&oacute; đội b&oacute;ng n&agrave;o kể từ&nbsp;<a href=\"http://www.24h.com.vn/manchester-united-c48e1521.html\">MU</a>&nbsp;năm 2009, bảo vệ th&agrave;nh c&ocirc;ng ng&ocirc;i v&ocirc; địch&nbsp;<strong>Ngoại hạng Anh</strong>. Khi đ&oacute;, trong tay Sir Alex l&agrave; đội qu&acirc;n xuất ch&uacute;ng bậc nhất lịch sử.&nbsp;H&agrave;ng thủ c&oacute; hai h&ograve;n đ&aacute; tảng Vidic - Ferdinand &aacute;n ngữ, sau lưng họ l&agrave; Van der Sar, ph&iacute;a trước l&agrave; Giggs, Scholes, Carrick. Nơi tuyến đầu l&agrave; bộ tứ nguy&ecirc;n tử Ronaldo - Rooney - Tevez - Berbatov. Đội b&oacute;ng ấy chỉ thất bại trước lời nguyền Champions League, c&ograve;n&nbsp;<a href=\"http://www.24h.com.vn/premier-league-2017-2018-c48e2367.html\">Premier League</a>&nbsp;th&igrave; kh&ocirc;ng.</p>\r\n\r\n<p><img alt=\"Thảm họa Chelsea: Lời nguyền khốc liệt, Conte lo mất ghế vào tay Ancelotti - 1\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-16/Tham-hoa-Chelsea-Loi-nguyen-ung-nghiem-Conte-lo-mat-ghe-vao-tay-Ancelotti-chelsea-manager-antonio-conte-1508108594-width660height347.jpg\" /></p>\r\n\r\n<p>Chỉ 5 th&aacute;ng trước, Conte c&ograve;n đang n&acirc;ng cao c&uacute;p v&ocirc; địch</p>\r\n\r\n<p>Nhưng sau n&agrave;y, ngay cả khi vẫn l&agrave; MU của Sir Alex, đều kh&ocirc;ng thể bước qua lời nguyền Ngoại hạng Anh. T&agrave;i năng như Jose Mourinho rồi cũng phải ra đi kh&ocirc;ng k&egrave;n trống. Tấn c&ocirc;ng rực lửa như Man City của Manuel Pellegrini cũng bị dập tắt. C&acirc;u chuyện thần thoại của Ranieri tại Leicester kết th&uacute;c theo c&aacute;i c&aacute;ch kh&ocirc;ng thể bi kịch hơn. T&oacute;m lại, mọi đội b&oacute;ng trong v&ograve;ng 8 năm qua đều đầu h&agrave;ng với lời nguyền d&agrave;nh cho nh&agrave; đương kim v&ocirc; địch Ngoại hạng Anh.</p>\r\n\r\n<p>Ở Chelsea lại c&ograve;n c&oacute; một lời nguyền kh&aacute;c nữa. Ng&agrave;y 23/10/2016, khi MU thua tan t&aacute;c 0-4 trước chủ nh&agrave; Chelsea. Khi rời Stamford Bridge, Mourinho chỉ giơ 3 ng&oacute;n tay về ph&iacute;a Conte. Người ta đồn đo&aacute;n rằng, chừng n&agrave;o c&ograve;n chưa mang về cho Chelsea 3 chức v&ocirc; địch&nbsp;<em>Ngoại hạng Anh</em>, Conte cũng chỉ l&agrave; c&aacute;i b&oacute;ng của Mourinho. Nhưng kh&ocirc;ng, h&oacute;a ra &ldquo;Người đặc biệt&rdquo; cảnh b&aacute;o cho kẻ kế nhiệm chiếc ghế của m&igrave;nh về sự khốc liệt.&nbsp;H&atilde;y cố gắng trụ lại qua m&ugrave;a thứ ba!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&Aacute;c mộng chờ Conte</strong></p>\r\n\r\n<p>Đến thời điểm n&agrave;y, Antonio Conte mới thấm th&iacute;a. &Ocirc;ng bắt đầu m&ugrave;a b&oacute;ng thứ hai theo c&aacute;i c&aacute;ch đầy nghi hoặc. Kh&acirc;u chuẩn bị giống như một thảm họa: Conte sa đ&agrave; v&agrave;o cuộc tranh c&atilde;i với Costa, sau đ&oacute; lại g&acirc;y tranh c&atilde;i khi b&aacute;n Matic cho MU. Cuối c&ugrave;ng, hễ Conte muốn ai Chelsea mua hụt người đ&oacute;, từ&nbsp;Chamberlain, Fernando Llorente, Lukaku, Aurier... đều đ&atilde; cự tuyệt Conte.</p>\r\n\r\n<p><img alt=\"Thảm họa Chelsea: Lời nguyền khốc liệt, Conte lo mất ghế vào tay Ancelotti - 2\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-16/Tham-hoa-Chelsea-Loi-nguyen-ung-nghiem-Conte-lo-mat-ghe-vao-tay-Ancelotti-2017-05-27t192918z_1684221200_rc12bdab0c70_rtrmadp-1508108671-width660height468.jpg\" /></p>\r\n\r\n<p>Chelsea đang l&acirc;m v&agrave;o một cuộc khủng hoảng mini</p>\r\n\r\n<p>Khoảng thời gian đ&atilde; qua giống như sự tiếp nối của cơn &aacute;c mộng. Chelsea với tư c&aacute;ch nh&agrave; v&ocirc; địch, thua sốc&nbsp; Burnley tr&ecirc;n s&acirc;n nh&agrave; trong ng&agrave;y khai m&agrave;n giải đấu. V&agrave; cho đến nay, khi giải đấu mới đi qua v&ograve;ng thứ 8, nh&agrave; đương kim v&ocirc; địch đ&atilde; thua đến 3 trận, 2 v&ograve;ng đấu đ&atilde; qua l&agrave; 2 thất bại li&ecirc;n tiếp.</p>\r\n\r\n<p>Nếu t&iacute;nh cả trận Si&ecirc;u c&uacute;p nước Anh, số trận thua đ&atilde; l&agrave; 4. Sir Alex với 27 năm l&agrave;m việc tại MU từng ước lượng, nếu thua qu&aacute; 3 trận trong 1 m&ugrave;a giải, đội b&oacute;ng ấy đừng mơ c&uacute;p bạc v&agrave;o cuối m&ugrave;a. Tức l&agrave; Conte đ&atilde; chạm đến v&ugrave;ng an to&agrave;n cuối c&ugrave;ng, đ&oacute; l&agrave; lằn ranh m&agrave; &ocirc;ng kh&ocirc;ng thể bước qua.</p>\r\n\r\n<p>Nhưng thậm ch&iacute; Conte c&ograve;n kh&ocirc;ng chắc đ&atilde; ở lại được đến cuối m&ugrave;a thứ hai. Chelsea hiện đang xếp thứ 5. Khoảng c&aacute;ch giữa họ v&agrave; đo&agrave;n qu&acirc;n vũ b&atilde;o của Pep Guardiola đ&atilde; l&agrave; 3 chiến thắng. Kh&ocirc;ng c&oacute; dấu hiệu n&agrave;o cho thấy Man City sẽ dừng lại, trong khi Chelsea bắt đầu manh nha cơn khủng hoảng.</p>\r\n\r\n<p>Nh&agrave; đương kim v&ocirc; địch văng khỏi top 4, thậm ch&iacute; bị một đội b&oacute;ng nhược tiểu như Watford xếp tr&ecirc;n c&oacute; thể xem như một lời cảnh b&aacute;o đối với Conte v&agrave; chiếc ghế của nh&agrave; cầm qu&acirc;n người &Yacute;. Xưa nay Roman Abramovich chưa bao giờ l&agrave; người biết ki&ecirc;n nhẫn.</p>\r\n', 'IMG_59e4719f41f5a7b01c249d8f27bd5191258db8e7c3249.jpg', '2017-10-16 03:10:19', NULL, 0),
(46, 2, 3, 'Kaka khóc như mưa ngày giải nghệ: Tạm biệt \"Soái ca\" Thiên thần', '<p><img alt=\"Kaka khóc như mưa ngày giải nghệ: Tạm biệt &amp;#34;Soái ca&amp;#34; Thiên thần - 1\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-16/1508116171-img_5958-750x400-660.jpg\" /></p>\r\n\r\n<p>Kaka kết th&uacute;c sự nghiệp với&nbsp;hơn 700 trận đấu</p>\r\n\r\n<p>Trong ng&agrave;y m&agrave; Kaka được thi đấu tr&ecirc;n s&acirc;n nh&agrave; của Orlando v&agrave; được c&aacute;c fan tri &acirc;n nồng nhiệt, đội kh&aacute;ch Columbus Crew đ&atilde; khiến bữa tiệc kh&ocirc;ng trọn vẹn khi Ola Kamara ghi b&agrave;n duy nhất để thắng 1-0. Nhưng trung t&acirc;m trận đấu vẫn l&agrave; Kaka, người đ&atilde; kh&ocirc;ng k&igrave;m được nước mắt ngay từ l&uacute;c khởi h&agrave;nh quốc ca trước trận đấu.</p>\r\n\r\n<p>Kaka đ&atilde; ghi 24 b&agrave;n &amp; 22 kiến tạo trong 74 trận ở Orlando. Mặc d&ugrave; Orlando City l&agrave; một đội b&oacute;ng mới ra đời kh&ocirc;ng được l&acirc;u, nhưng sự hiện diện của Kaka đ&atilde; gi&uacute;p CLB n&agrave;y thi đấu kh&aacute; tốt d&ugrave; tiếc rằng họ li&ecirc;n tiếp hụt v&eacute; dự playoff ở miền Đ&ocirc;ng của MLS. Kaka được chọn v&agrave;o đội h&igrave;nh to&agrave;n sao (All-Star) của MLS trong 3 năm li&ecirc;n tiếp v&agrave; l&agrave; Cầu thủ xuất sắc nhất của trận All-Star năm 2015 khi anh v&agrave; c&aacute;c đồng đội đ&aacute;nh bại Tottenham 2-1.</p>\r\n\r\n<p>Orlando City l&agrave; điểm dừng ch&acirc;n của Kaka từ năm 2014 sau khi anh kết th&uacute;c sự nghiệp thi đấu ở ch&acirc;u &Acirc;u trong m&agrave;u &aacute;o AC Milan, CLB m&agrave; anh đ&atilde; kho&aacute;c &aacute;o trong giai đoạn 2003 &ndash; 2009 v&agrave; đoạt 1 chức v&ocirc; địch Serie A lẫn một Champions League. Cũng tại Milan, Kaka được vinh danh l&agrave; Quả b&oacute;ng V&agrave;ng của năm 2007, lần gần nhất một cầu thủ kh&aacute;c ngo&agrave;i&nbsp;<em>Cristiano Ronaldo v&agrave; Lionel Messi</em>&nbsp;đoạt danh hiệu n&agrave;y.</p>\r\n\r\n<p>Kaka gia nhập Real Madrid năm 2009 v&agrave; mặc d&ugrave; đoạt 3 chức v&ocirc; địch tại Bernabeu, sự nghiệp của anh đi xuống do chấn thương h&agrave;nh hạ. Sau 1 m&ugrave;a giải trở lại Milan, Kaka tới Orlando do sự mời ch&agrave;o của &ocirc;ng chủ người Brazil của CLB, cũng như c&oacute; một thời gian ngắn trở lại CLB cũ Sao Paulo dưới dạng cho mượn năm 2014.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kaka lần đầu ti&ecirc;n kho&aacute;c &aacute;o ĐT Brazil năm 2002 v&agrave; được triệu tập cho chiến dịch World Cup th&agrave;nh c&ocirc;ng năm 2002, cũng như hiện diện ở hai kỳ World Cup 2006 v&agrave; 2010, dẫn đầu hạng mục kiến tạo ở năm 2010. Anh cũng g&oacute;p mặt khi ĐT Brazil v&ocirc; địch Confederations Cup năm 2005 v&agrave; 2009.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kaka khóc như mưa ngày giải nghệ: Tạm biệt &amp;#34;Soái ca&amp;#34; Thiên thần - 2\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-16/1508116493-10-16-2017-8-07-36-am-660.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đồng đội &ocirc;m lấy Kaka trong những gi&acirc;y ph&uacute;t cuối c&ugrave;ng của anh tr&ecirc;n s&acirc;n b&oacute;ng</p>\r\n\r\n<p>Được xem l&agrave; một trong những cầu thủ xuất sắc nhất trong thế hệ của m&igrave;nh, anh c&ograve;n nổi tiếng với những hoạt động nh&acirc;n đạo khi trở th&agrave;nh đại sứ trẻ nhất của chương tr&igrave;nh Cứu trợ lương thực Thế giới của Li&ecirc;n Hợp Quốc năm 2004. Danh tiếng của&nbsp;Kaka&nbsp;vượt ra ngo&agrave;i&nbsp;<a href=\"http://www.24h.com.vn/bong-da-c48.html\">b&oacute;ng đ&aacute;</a>&nbsp;khi anh l&agrave; VĐV thể thao đầu ti&ecirc;n đạt 10 triệu người theo d&otilde;i tr&ecirc;n Twitter, v&agrave; anh được ESPN bầu chọn l&agrave; VĐV nổi tiếng thứ 18 tr&ecirc;n to&agrave;n thế giới.</p>\r\n\r\n<p>Kaka cho biết cơ thể anh đ&atilde; chịu kh&aacute; nhiều đau đớn trong thời gian gần đ&acirc;y v&agrave; cảm thấy kh&ocirc;ng thể tiếp tục hơn nữa. &quot;T&ocirc;i sẽ d&agrave;nh thời gian nghỉ ngơi, v&agrave; c&oacute; lẽ t&ocirc;i sẽ theo bước Zidane l&agrave;m huấn luyện vi&ecirc;n&quot;, Kaka n&oacute;i.</p>\r\n\r\n<p><em>Những khoảnh khắc đ&aacute;ng nhớ của Kaka trong m&agrave;u &aacute;o Orlando City</em></p>\r\n', 'IMG_59e471b612703ba1241d26f4b4a12c0de5caaed57e94c.jpg', '2017-10-16 03:10:42', NULL, 0),
(47, 2, 3, 'Kaka khóc như mưa ngày giải nghệ: Tạm biệt \"Soái ca\" Thiên thần', '<p><img alt=\"Kaka khóc như mưa ngày giải nghệ: Tạm biệt &amp;#34;Soái ca&amp;#34; Thiên thần - 1\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-16/1508116171-img_5958-750x400-660.jpg\" /></p>\r\n\r\n<p>Kaka kết th&uacute;c sự nghiệp với&nbsp;hơn 700 trận đấu</p>\r\n\r\n<p>Trong ng&agrave;y m&agrave; Kaka được thi đấu tr&ecirc;n s&acirc;n nh&agrave; của Orlando v&agrave; được c&aacute;c fan tri &acirc;n nồng nhiệt, đội kh&aacute;ch Columbus Crew đ&atilde; khiến bữa tiệc kh&ocirc;ng trọn vẹn khi Ola Kamara ghi b&agrave;n duy nhất để thắng 1-0. Nhưng trung t&acirc;m trận đấu vẫn l&agrave; Kaka, người đ&atilde; kh&ocirc;ng k&igrave;m được nước mắt ngay từ l&uacute;c khởi h&agrave;nh quốc ca trước trận đấu.</p>\r\n\r\n<p>Kaka đ&atilde; ghi 24 b&agrave;n &amp; 22 kiến tạo trong 74 trận ở Orlando. Mặc d&ugrave; Orlando City l&agrave; một đội b&oacute;ng mới ra đời kh&ocirc;ng được l&acirc;u, nhưng sự hiện diện của Kaka đ&atilde; gi&uacute;p CLB n&agrave;y thi đấu kh&aacute; tốt d&ugrave; tiếc rằng họ li&ecirc;n tiếp hụt v&eacute; dự playoff ở miền Đ&ocirc;ng của MLS. Kaka được chọn v&agrave;o đội h&igrave;nh to&agrave;n sao (All-Star) của MLS trong 3 năm li&ecirc;n tiếp v&agrave; l&agrave; Cầu thủ xuất sắc nhất của trận All-Star năm 2015 khi anh v&agrave; c&aacute;c đồng đội đ&aacute;nh bại Tottenham 2-1.</p>\r\n\r\n<p>Orlando City l&agrave; điểm dừng ch&acirc;n của Kaka từ năm 2014 sau khi anh kết th&uacute;c sự nghiệp thi đấu ở ch&acirc;u &Acirc;u trong m&agrave;u &aacute;o AC Milan, CLB m&agrave; anh đ&atilde; kho&aacute;c &aacute;o trong giai đoạn 2003 &ndash; 2009 v&agrave; đoạt 1 chức v&ocirc; địch Serie A lẫn một Champions League. Cũng tại Milan, Kaka được vinh danh l&agrave; Quả b&oacute;ng V&agrave;ng của năm 2007, lần gần nhất một cầu thủ kh&aacute;c ngo&agrave;i&nbsp;<em>Cristiano Ronaldo v&agrave; Lionel Messi</em>&nbsp;đoạt danh hiệu n&agrave;y.</p>\r\n\r\n<p>Kaka gia nhập Real Madrid năm 2009 v&agrave; mặc d&ugrave; đoạt 3 chức v&ocirc; địch tại Bernabeu, sự nghiệp của anh đi xuống do chấn thương h&agrave;nh hạ. Sau 1 m&ugrave;a giải trở lại Milan, Kaka tới Orlando do sự mời ch&agrave;o của &ocirc;ng chủ người Brazil của CLB, cũng như c&oacute; một thời gian ngắn trở lại CLB cũ Sao Paulo dưới dạng cho mượn năm 2014.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kaka lần đầu ti&ecirc;n kho&aacute;c &aacute;o ĐT Brazil năm 2002 v&agrave; được triệu tập cho chiến dịch World Cup th&agrave;nh c&ocirc;ng năm 2002, cũng như hiện diện ở hai kỳ World Cup 2006 v&agrave; 2010, dẫn đầu hạng mục kiến tạo ở năm 2010. Anh cũng g&oacute;p mặt khi ĐT Brazil v&ocirc; địch Confederations Cup năm 2005 v&agrave; 2009.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Kaka khóc như mưa ngày giải nghệ: Tạm biệt &amp;#34;Soái ca&amp;#34; Thiên thần - 2\" src=\"http://image.24h.com.vn/upload/4-2017/images/2017-10-16/1508116493-10-16-2017-8-07-36-am-660.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đồng đội &ocirc;m lấy Kaka trong những gi&acirc;y ph&uacute;t cuối c&ugrave;ng của anh tr&ecirc;n s&acirc;n b&oacute;ng</p>\r\n\r\n<p>Được xem l&agrave; một trong những cầu thủ xuất sắc nhất trong thế hệ của m&igrave;nh, anh c&ograve;n nổi tiếng với những hoạt động nh&acirc;n đạo khi trở th&agrave;nh đại sứ trẻ nhất của chương tr&igrave;nh Cứu trợ lương thực Thế giới của Li&ecirc;n Hợp Quốc năm 2004. Danh tiếng của&nbsp;Kaka&nbsp;vượt ra ngo&agrave;i&nbsp;<a href=\"http://www.24h.com.vn/bong-da-c48.html\">b&oacute;ng đ&aacute;</a>&nbsp;khi anh l&agrave; VĐV thể thao đầu ti&ecirc;n đạt 10 triệu người theo d&otilde;i tr&ecirc;n Twitter, v&agrave; anh được ESPN bầu chọn l&agrave; VĐV nổi tiếng thứ 18 tr&ecirc;n to&agrave;n thế giới.</p>\r\n\r\n<p>Kaka cho biết cơ thể anh đ&atilde; chịu kh&aacute; nhiều đau đớn trong thời gian gần đ&acirc;y v&agrave; cảm thấy kh&ocirc;ng thể tiếp tục hơn nữa. &quot;T&ocirc;i sẽ d&agrave;nh thời gian nghỉ ngơi, v&agrave; c&oacute; lẽ t&ocirc;i sẽ theo bước Zidane l&agrave;m huấn luyện vi&ecirc;n&quot;, Kaka n&oacute;i.</p>\r\n\r\n<p><em>Những khoảnh khắc đ&aacute;ng nhớ của Kaka trong m&agrave;u &aacute;o Orlando City</em></p>\r\n', 'IMG_59e471c89873515f02a5bbdad18c3ab8af5fc7dccad03.jpg', '2017-10-16 03:10:00', NULL, 0),
